void app_init(void)
{
  for(uint32_t i = 0; i < 10000000; i++ );

  sleep_delay_init();
  spi_init_init();

}

/***************************************************************************//**
 * App ticking function.
 ******************************************************************************/
void app_process_action(void)
{

  if (isUpdateDone == false){

    // just draw the picture
    eink_drv_update_image((uint8_t*)&dtm1_buffer,
                          (uint8_t*)&dtm2_buffer,
                          BUFFER_SIZE);

    // prepare the buffer to became red
    for(uint16_t i = 0; i < BUFFER_SIZE; i++){
      dtm1_buffer[i] = 0xFF;
      dtm2_buffer[i] = 0x00;
    }
    eink_drv_update_image((uint8_t*)&dtm1_buffer,
                          (uint8_t*)&dtm2_buffer,
                          BUFFER_SIZE);

    // prepare the buffer to became black
    for(uint16_t i = 0; i < BUFFER_SIZE; i++){
      dtm1_buffer[i] = 0x00;
      dtm2_buffer[i] = 0xFF;
    }
    eink_drv_update_image((uint8_t*)&dtm1_buffer,
                          (uint8_t*)&dtm2_buffer,
                          BUFFER_SIZE);

    // prepare the buffer to became white
    for(uint16_t i = 0; i < BUFFER_SIZE; i++){
      dtm1_buffer[i] = 0xFF;
      dtm2_buffer[i] = 0xFF;
    }
    eink_drv_update_image((uint8_t*)&dtm1_buffer,
                          (uint8_t*)&dtm2_buffer,
                          BUFFER_SIZE);

    isUpdateDone = true;
  }
}
